                <script>
                    let workShopOverviewList = document.querySelector('.workshop-list');
                    let workBenchList = document.querySelector('.work-bench-list');
                    let appontmentList = document.querySelector('.appointment-list');
                    let financialList = document.querySelector('.financials-list');

                    let workshopSec = document.querySelector(".work-shop-sec");
                    let workbenchSec = document.querySelector(".work-bench-sec");
                    let appointmentSec = document.querySelector(".appointment-sec");
                    let financialsSec = document.querySelector(".financials-sec");


                    workShopOverviewList.addEventListener('click', () => {
                        workShopOverviewList.classList.add('bbd');
                        workBenchList.classList.remove('bbd');
                        appontmentList.classList.remove('bbd');
                        financialList.classList.remove('bbd');

                        workshopSec.style.display = 'block';
                        workbenchSec.style.display = 'none';
                        appointmentSec.style.display = 'none';
                        financialsSec.style.display = 'none';
                    });
                    workBenchList.addEventListener('click', () => {
                        workShopOverviewList.classList.remove('bbd');
                        workBenchList.classList.add('bbd');
                        appontmentList.classList.remove('bbd');
                        financialList.classList.remove('bbd');

                        workshopSec.style.display = 'none';
                        workbenchSec.style.display = 'block';
                        appointmentSec.style.display = 'none';
                        financialsSec.style.display = 'none';
                    });
                    appontmentList.addEventListener('click', () => {
                        workShopOverviewList.classList.remove('bbd');
                        workBenchList.classList.remove('bbd');
                        appontmentList.classList.add('bbd');
                        financialList.classList.remove('bbd');

                        workshopSec.style.display = 'none';
                        workbenchSec.style.display = 'none';
                        appointmentSec.style.display = 'block';
                        financialsSec.style.display = 'none';
                    });
                    financialList.addEventListener('click', () => {
                        workShopOverviewList.classList.remove('bbd');
                        workBenchList.classList.add('bbd');
                        appontmentList.classList.add('bbd');
                        financialList.classList.add('bbd');

                        workshopSec.style.display = 'none';
                        workbenchSec.style.display = 'none';
                        appointmentSec.style.display = 'none';
                        financialsSec.style.display = 'block';
                    });
                </script>
